mapper.select:
SELECT is1.issueID,is1.title,is1.creater,is1.createtime,usertable.name,is1.issuestate,is1.plantime,is1.acttime FROM (SELECT * FROM issuetable WHERE userID=#{userID}) AS is1,usertable
AND issueID = #{issueID}
AND issuestate LIKE '%' #{issuestate} '%'
AND creater LIKE '%' #{creater} '%'
AND name LIKE '%' #{name} '%'
AND createtime >= #{createtime}
AND createtime <= #{createtime1}
AND plantime >= #{plantime}
AND plantime <= #{plantime1}
AND is1.userID=usertable.userID
ORDER BY is1.issueID DESC

mapper.update:
UPDATE issuetable
SET
title=#{title},type=#{type},level=#{level},version=#{version},acttime=#{acttime},step=#{step},issuestate=#{issuestate}
WHERE userID=#{userID} AND issueID=#{issueID}

UPDATE issuetable
SET solution=#{solution}
WHERE
userID=#{userID} AND issueID=#{issueID}

UPDATE issuetable
SET issuestate=#{issuestate}
WHERE
userID=#{userID} AND issueID=#{issueID}
