mapper.update:
UPDATE issuetable
SET
title=#{title},type=#{type},level=#{level},version=#{version},acttime=#{acttime},step=#{step},issuestate=#{issuestate}
WHERE userID=#{userID} AND issueID=#{issueID}

UPDATE issuetable
SET solution=#{solution}
WHERE userID=#{userID} AND issueID=#{issueID}

UPDATE issuetable
SET issuestate=#{issuestate}
WHERE userID=#{userID} AND issueID=#{issueID}
